import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { api } from '../services/api';
import { 
  ArrowLeft, 
  Mail, 
  RotateCcw, 
  Plus,
  Calendar,
  User,
  Phone,
  MapPin,
  Building2
} from 'lucide-react';
import ActivityModal from '../components/ActivityModal';
import StatusModal from '../components/StatusModal';

function LeadDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('atividades');
  const [isActivityModalOpen, setIsActivityModalOpen] = useState(false);
  const [isStatusModalOpen, setIsStatusModalOpen] = useState(false);
  const [atividades, setAtividades] = useState([]);

  // Buscar dados reais do expositor
  const { data: leadData, isLoading, isError } = useQuery(
    ['lead', id],
    async () => {
      const res = await api.get(`/expositores/${id}`);
      return res.data;
    },
    { enabled: !!id, refetchOnWindowFocus: false, retry: 1 }
  );
  if (isLoading) {
    return (
      <div className="h-full flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (isError || !leadData) {
    return (
      <div className="h-full flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="text-red-500 text-6xl mb-4">丘멆잺</div>
          <p className="text-red-600 mb-2">Erro ao carregar lead</p>
          <button
            onClick={() => navigate('/kanban')}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Voltar ao Kanban
          </button>
        </div>
      </div>
    );
  }

  const leadInfo = {
    id: leadData.id,
    nome: leadData.nomeFantasia || leadData.razaoSocial,
    empresa: leadData.razaoSocial || '',
    email: leadData.email || '',
    telefone: leadData.telefone || leadData.celular || '',
    endereco: [leadData.endereco, leadData.cidade, leadData.estado].filter(Boolean).join(' - '),
    status: leadData.status,
    vendedor: leadData.vendedor?.nome || 'N칚o atribu칤do',
    dataCriacao: leadData.dataCadastro ? new Date(leadData.dataCadastro).toLocaleDateString('pt-BR') : '',
    valorEstimado: ''
  };


  const tabs = [
    { id: 'atividades', label: 'Atividades' },
    { id: 'dados', label: 'Dados' },
    { id: 'contatos', label: 'Lista Contatos' },
    { id: 'retomada', label: 'Retomada' }
  ];

  const handleSaveActivity = async (activityData) => {
    console.log('Atividade salva para o lead:', id, activityData);

    try {
      // Salvar atividade no backend
      const response = await api.post('/agenda/atividades', {
        ...activityData,
        leadId: `lead-${id}`
      });

      if (response.data.sucesso) {
        // Criar nova atividade para exibir na tabela local
        const novaAtividade = {
          id: response.data.id,
          data: activityData.dataCriacao || new Date().toLocaleDateString('pt-BR'),
          descricao: activityData.descricao,
          agendamento: activityData.dataAgendamento && activityData.horarioAgendamento 
            ? `Sim - ${activityData.dataAgendamento}` 
            : 'N칚o',
          usuario: 'Administrador', // Pode ser din칙mico baseado no usu치rio logado
          tipo: activityData.tipoAtividade,
          link: activityData.link
        };

        // Adicionar  lista de atividades
        setAtividades(prevAtividades => [novaAtividade, ...prevAtividades]);

        setIsActivityModalOpen(false);

        // Mostrar mensagem de sucesso
        console.log('Atividade salva com sucesso na agenda!');
      }
    } catch (error) {
      console.error('Erro ao salvar atividade:', error);
      // Ainda adicionar localmente em caso de erro
      const novaAtividade = {
        id: Date.now(),
        data: activityData.dataCriacao || new Date().toLocaleDateString('pt-BR'),
        descricao: activityData.descricao,
        agendamento: activityData.dataAgendamento && activityData.horarioAgendamento 
          ? `Sim - ${activityData.dataAgendamento}` 
          : 'N칚o',
        usuario: 'Administrador',
        tipo: activityData.tipoAtividade,
        link: activityData.link
      };
      setAtividades(prevAtividades => [novaAtividade, ...prevAtividades]);
      setIsActivityModalOpen(false);
    }
  };

  const handleSaveStatus = (statusData) => {
    console.log('游닇 Status alterado para o lead:', id, statusData);

    // Preparar dados da mudan칞a de status
    const statusChange = {
      leadId: id,
      newStatus: statusData.newStatus,
      timestamp: new Date().toISOString()
    };

    console.log('游 Disparando evento statusChanged:', statusChange);

    // Disparar evento customizado para notificar o Kanban
    const event = new CustomEvent('statusChanged', { 
      detail: statusChange 
    });
    window.dispatchEvent(event);

    // Salvar no localStorage como backup
    localStorage.setItem('statusChange', JSON.stringify(statusChange));

    // Fechar modal
    setIsStatusModalOpen(false);

    // Navegar de volta para o Kanban para ver a mudan칞a
    setTimeout(() => {
      console.log('游댃 Navegando de volta para o Kanban...');
      navigate('/kanban');
    }, 200);
  };

  return (
    <div className="h-full flex flex-col bg-white">
      {/* Header com navega칞칚o */}
      <div className="bg-blue-100 px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button 
              onClick={() => navigate('/kanban')}
              className="p-2 hover:bg-blue-200 rounded-lg transition-colors"
            >
              <ArrowLeft className="h-5 w-5 text-gray-600" />
            </button>
            <div>
              <h1 className="text-xl font-bold text-gray-800">Detalhes do Lead</h1>
              <p className="text-sm text-gray-600">{leadData.nomeFantasia || leadData.razaoSocial} - {leadData.razaoSocial}</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <button className="bg-green-600 hover:bg-green-700 text-white p-2 rounded-lg transition-colors">
              <Mail className="h-5 w-5" />
            </button>
            <button 
              onClick={() => setIsStatusModalOpen(true)}
              className="bg-green-600 hover:bg-green-700 text-white p-2 rounded-lg transition-colors"
              title="Alterar Status do Lead"
            >
              <RotateCcw className="h-5 w-5" />
            </button>
            <button 
              onClick={() => setIsActivityModalOpen(true)}
              className="bg-blue-600 hover:bg-blue-700 text-white p-2 rounded-lg transition-colors"
            >
              <Plus className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Tabs de navega칞칚o */}
      <div className="border-b border-gray-200">
        <nav className="flex space-x-8 px-6">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === tab.id
                  ? 'border-blue-600 text-blue-600'
                  : 'border-transparent text-blue-400 hover:text-blue-600 hover:border-gray-300'
              }`}
            >
              {tab.label}
            </button>
          ))}
        </nav>
      </div>

      {/* Conte칰do das abas */}
      <div className="flex-1 p-6">
        {activeTab === 'atividades' && (
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold text-gray-900">Registro de Atividades</h2>
              <div className="bg-blue-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                {leadData.vendedor?.nome || 'N칚o atribu칤do'}
              </div>
            </div>

            {/* Tabela de atividades */}
            <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
              {atividades.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <div className="w-12 h-12 mx-auto mb-4 bg-gray-200 rounded-lg flex items-center justify-center">
                    <div className="w-6 h-6 bg-gray-400 rounded-sm"></div>
                  </div>
                  <p className="text-sm">Nenhuma atividade registrada ainda</p>
                  <p className="text-xs text-gray-400 mt-1">Clique no bot칚o "+" para adicionar uma atividade</p>
                </div>
              ) : (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Dt. Atividade
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Tipo
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Descri칞칚o
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Agendamento
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Usu치rio
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {atividades.map((atividade) => (
                      <tr key={atividade.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {atividade.data}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                            {atividade.tipo}
                          </span>
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-900">
                          {atividade.descricao}
                          {atividade.link && (
                            <div className="mt-1">
                              <a 
                                href={atividade.link} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="text-blue-600 hover:text-blue-800 text-xs underline"
                              >
                                Ver link
                              </a>
                            </div>
                          )}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {atividade.agendamento}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-100 text-blue-800">
                            {atividade.usuario}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        )}

        {activeTab === 'dados' && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold text-gray-900">Dados do Lead</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="bg-gray-50 p-6 rounded-lg">
                <h3 className="text-md font-medium text-gray-900 mb-4">Informa칞칫es Pessoais</h3>
                <div className="space-y-3">
                  <div className="flex items-center space-x-3">
                    <User className="h-5 w-5 text-gray-400" />
                    <span className="text-sm text-gray-600">{leadData.nomeFantasia || leadData.razaoSocial}</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <Mail className="h-5 w-5 text-gray-400" />
                    <span className="text-sm text-gray-600">{leadData.email}</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <Phone className="h-5 w-5 text-gray-400" />
                    <span className="text-sm text-gray-600">{leadData.telefone || leadData.celular}</span>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 p-6 rounded-lg">
                <h3 className="text-md font-medium text-gray-900 mb-4">Informa칞칫es da Empresa</h3>
                <div className="space-y-3">
                  <div className="flex items-center space-x-3">
                    <Building2 className="h-5 w-5 text-gray-400" />
                    <span className="text-sm text-gray-600">{leadData.empresa}</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <MapPin className="h-5 w-5 text-gray-400" />
                    <span className="text-sm text-gray-600">{leadData.endereco}</span>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 p-6 rounded-lg">
                <h3 className="text-md font-medium text-gray-900 mb-4">Status e Vendas</h3>
                <div className="space-y-3">
                  <div>
                    <span className="text-sm font-medium text-gray-500">Status:</span>
                    <span className="ml-2 text-sm text-gray-600">{leadData.status}</span>
                  </div>
                  <div>
                    <span className="text-sm font-medium text-gray-500">Vendedor:</span>
                    <span className="ml-2 text-sm text-gray-600">{leadData.vendedor}</span>
                  </div>
                  <div>
                    <span className="text-sm font-medium text-gray-500">Valor Estimado:</span>
                    <span className="ml-2 text-sm text-gray-600">{leadData.valorEstimado}</span>
